import { useState } from "react";

const UseStateCounter = () => {
  const [count, setCount] = useState(1);
  const handleClick = (count) => {
    setCount(count + 1);
  };
  return (
    <>
      <button type="button" onClick={() => handleClick(count)}>
        Add to count
      </button>
      <p>{count}</p>
    </>
  );
};

export default UseStateCounter;
