import React, { useState, useEffect } from "react";
const url = "https://api.github.com/users/QuincyLarson";
const MultipleReturns = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(true);
  const [user, setUser] = useState("Default User");

  useEffect(() => {
    fetch(url).then((resp) => {
      //promise console.log(resp.json());
      resp
        .json()
        .then((user) => {
          console.log(user);
          setUser(user.login);
          setIsLoading(false);
        })
        .catch((error) => {
          setIsError(true);
          console.log(error);
        });
    });
  }, [isLoading]);

  if (isLoading) {
    return (
      <div>
        <h1>Loading...</h1>
      </div>
    );
  }
  if (isError) {
    return <h2>Error...</h2>;
  }

  return (
    <div>
      <h1>{user}</h1>
    </div>
  );
};

export default MultipleReturns;
